import { Component } from '@angular/core';
import { EditProfileModalComponent } from './edit-profile-modal.component';

interface User {
  username: string;
  name: string;
  email: string;
  profile: string[];
  minCH: number | null;
  status: 'Ativo' | 'Suspenso';
}

@Component({
  selector: 'app-user-table',
  standalone: true,
  imports: [EditProfileModalComponent],
  templateUrl: './user-table.component.html',
  styleUrls: ['./user-table.component.scss']
})
export class UserTableComponent {
  users: User[] = [
    {
      username: 'aelism',
      name: 'Ant√¥nio Elias de Melo',
      email: 'antonio@inf.ufg.br',
      profile: ['DOCENTE'],
      minCH: 64,
      status: 'Ativo'
    },
    {
      username: 'joao',
      name: 'Jo√£o Batista',
      email: 'joao@inf.ufg.br',
      profile: ['DIRETOR'],
      minCH: null,
      status: 'Ativo'
    },
    {
      username: 'luteles',
      name: 'Luciana de Oliveira Teles',
      email: 'luteles@inf.ufg.br',
      profile: ['DOCENTE'],
      minCH: 64,
      status: 'Suspenso'
    }
  ];

  profileOptions = [
    { label: 'DOCENTE', value: 'DOCENTE' },
    { label: 'VICE-DIRETOR(A)', value: 'VICE-DIRETOR(A)' }
  ];

  showProfileModal = false;
  selectedUser: User | null = null;
  selectedProfiles: string[] = [];

  openProfileModal(user: User) {
    this.selectedUser = user;
    this.selectedProfiles = [...user.profile];
    this.showProfileModal = true;
  }

  closeProfileModal() {
    this.showProfileModal = false;
    this.selectedUser = null;
    this.selectedProfiles = [];
  }

  saveProfileModal(profiles: string[]) {
    if (this.selectedUser) {
      this.selectedUser.profile = profiles;
    }
    this.closeProfileModal();
  }
}
